export const getServerSideProps: GetServerSideProps = async ({ req }) => {
  const { valid } = await checkIp(req);

  return {
    props: { isBot: valid },
    ...(!valid
      ? {
        redirect: {
          destination: process.env.NEXT_PUBLIC_EXIT_URL,
          permanent: false,
        },
      }
      : {}),
  };
};

